import { useLocation, useNavigate } from 'react-router-dom';
import { useState } from 'react';

export default function Payment() {
  const location = useLocation();
  const navigate = useNavigate();
  const movie = location.state?.movie ?? 'Selected movie';
  const [method, setMethod] = useState('UPI');

  function handlePay() {
    alert(`Payment of ${movie} via ${method} completed.`);
    navigate('/booking');
  }

  return (
    <div className="card">
      <h2 style={{marginTop:0}}>Payment</h2>
      <div style={{marginBottom:8}}>Movie: <strong>{movie}</strong></div>
      <label style={{display:'block',marginBottom:8}}>Payment method</label>
      <select className="input" value={method} onChange={e => setMethod(e.target.value)}>
        <option>UPI</option>
        <option>Card</option>
      </select>
      <div style={{marginTop:12}}>
        <button className="btn primary" onClick={handlePay}>Pay Now</button>
        <button className="btn ghost" style={{marginLeft:8}} onClick={() => navigate('/booking')}>Cancel</button>
      </div>
    </div>
  );
}
