import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

export default function Booking() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('All');

  const movies = [
    {
      id: 1,
      title: 'Avengers: Endgame',
      genre: 'Action',
      rating: 8.4,
      duration: '3h 1m',
      language: 'English',
      price: 250,
      image: 'ü¶∏',
      showTimes: ['10:30 AM', '2:15 PM', '6:45 PM', '9:30 PM']
    },
    {
      id: 2,
      title: 'Interstellar',
      genre: 'Sci-Fi',
      rating: 8.6,
      duration: '2h 49m',
      language: 'English',
      price: 300,
      image: 'üöÄ',
      showTimes: ['11:00 AM', '3:00 PM', '7:15 PM', '10:00 PM']
    },
    {
      id: 3,
      title: 'KGF Chapter 2',
      genre: 'Drama',
      rating: 8.2,
      duration: '2h 55m',
      language: 'Kannada',
      price: 220,
      image: '‚öîÔ∏è',
      showTimes: ['9:45 AM', '1:30 PM', '5:00 PM', '8:30 PM']
    },
    {
      id: 4,
      title: 'The Dark Knight',
      genre: 'Action',
      rating: 9.0,
      duration: '2h 32m',
      language: 'English',
      price: 280,
      image: 'ü¶á',
      showTimes: ['10:15 AM', '2:45 PM', '6:30 PM', '9:15 PM']
    },
    {
      id: 5,
      title: 'Inception',
      genre: 'Sci-Fi',
      rating: 8.8,
      duration: '2h 28m',
      language: 'English',
      price: 290,
      image: 'üí≠',
      showTimes: ['10:00 AM', '1:45 PM', '5:30 PM', '8:45 PM']
    },
    {
      id: 6,
      title: 'Dune',
      genre: 'Sci-Fi',
      rating: 8.0,
      duration: '2h 46m',
      language: 'English',
      price: 320,
      image: 'üèúÔ∏è',
      showTimes: ['11:30 AM', '3:15 PM', '7:00 PM', '10:15 PM']
    }
  ];

  const genres = ['All', 'Action', 'Sci-Fi', 'Drama'];

  const filteredMovies = movies.filter(movie => {
    const matchesSearch = movie.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesGenre = selectedGenre === 'All' || movie.genre === selectedGenre;
    return matchesSearch && matchesGenre;
  });

  function handleSelectShowtime(movie, showtime) {
    navigate('/payment', { 
      state: { 
        movie: movie.title,
        movieId: movie.id,
        price: movie.price,
        showtime,
        seats: 1
      } 
    });
  }

  return (
    <div>
      <h2 style={{marginBottom: 20}}>Select Your Movie</h2>
      
      {/* Search and Filter */}
      <div style={{marginBottom: 20, display: 'flex', gap: 12, flexWrap: 'wrap', alignItems: 'center'}}>
        <input 
          className="input" 
          placeholder="Search movies..." 
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          style={{flex: 1, minWidth: 200}}
        />
        <div style={{display: 'flex', gap: 8}}>
          {genres.map(genre => (
            <button
              key={genre}
              onClick={() => setSelectedGenre(genre)}
              style={{
                padding: '8px 14px',
                borderRadius: 8,
                border: '1px solid rgba(255,255,255,0.05)',
                background: selectedGenre === genre ? '#ff6b6b' : 'rgba(255,255,255,0.02)',
                color: selectedGenre === genre ? '#081025' : 'inherit',
                cursor: 'pointer',
                fontSize: 13
              }}
            >
              {genre}
            </button>
          ))}
        </div>
      </div>

      {/* Movies Grid */}
      <div className="movies">
        {filteredMovies.length > 0 ? (
          filteredMovies.map(movie => (
            <div key={movie.id} className="card" style={{flex: '1 1 280px', padding: 0, overflow: 'hidden'}}>
              {/* Movie Poster */}
              <div style={{
                background: 'linear-gradient(135deg, rgba(255,107,107,0.1), rgba(255,143,107,0.1))',
                padding: '20px',
                textAlign: 'center',
                fontSize: '48px',
                borderBottom: '1px solid rgba(255,255,255,0.03)'
              }}>
                {movie.image}
              </div>

              {/* Movie Info */}
              <div style={{padding: '16px'}}>
                <div className="movie-title" style={{fontSize: 16, marginBottom: 4}}>{movie.title}</div>
                
                <div style={{display: 'flex', justifyContent: 'space-between', fontSize: 12, color: '#9aa4b2', marginBottom: 8}}>
                  <span>{movie.genre}</span>
                  <span>‚≠ê {movie.rating}</span>
                </div>

                <div style={{fontSize: 12, color: '#9aa4b2', marginBottom: 8}}>
                  {movie.duration} ‚Ä¢ {movie.language}
                </div>

                <div style={{fontSize: 14, fontWeight: 600, color: '#ff6b6b', marginBottom: 12}}>
                  ‚Çπ{movie.price} per seat
                </div>

                <div style={{marginBottom: 12}}>
                  <label style={{display: 'block', fontSize: 12, marginBottom: 6, color: '#9aa4b2'}}>Select Showtime</label>
                  <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 6}}>
                    {movie.showTimes.map((time, idx) => (
                      <button
                        key={idx}
                        onClick={() => handleSelectShowtime(movie, time)}
                        style={{
                          padding: '6px 10px',
                          fontSize: 11,
                          borderRadius: 6,
                          border: '1px solid rgba(255,255,255,0.05)',
                          background: 'rgba(255,255,255,0.02)',
                          color: 'inherit',
                          cursor: 'pointer'
                        }}
                      >
                        {time}
                      </button>
                    ))}
                  </div>
                </div>

                <button 
                  className="btn primary" 
                  onClick={() => handleSelectShowtime(movie, movie.showTimes[0])}
                  style={{width: '100%'}}
                >
                  Book Now
                </button>
              </div>
            </div>
          ))
        ) : (
          <div style={{gridColumn: '1/-1', textAlign: 'center', color: '#9aa4b2', padding: 40}}>
            No movies found
          </div>
        )}
      </div>
    </div>
  );
}
