import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { isAuthenticated, decodeToken, logout } from '../utils/auth';

export default function User(){
  const [claims, setClaims] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    setClaims(decodeToken());
  }, []);

  function handleLogout(){
    logout();
    navigate('/');
  }

  if (!isAuthenticated()){
    return (
      <div className="card auth">
        <h2>Not signed in</h2>
        <p>You need to <Link to="/">login</Link> to view your profile.</p>
      </div>
    );
  }

  return (
    <div className="card auth">
      <h2>Your Profile</h2>
      {claims ? (
        <div style={{textAlign:'left',display:'flex',alignItems:'center',gap:14}}>
          <div className="avatar small">{(claims.sub||claims.email||'U').charAt(0).toUpperCase()}</div>
          <div>
            <div><strong>{claims.sub || claims.identity || claims.email}</strong></div>
            {claims.exp && <div style={{fontSize:12,color:'var(--muted)'}}>Token expires: {new Date(claims.exp * 1000).toLocaleString()}</div>}
            <div style={{marginTop:12}}>
              <button className="btn primary" onClick={() => navigate('/booking')}>My Bookings</button>
              <button className="btn ghost" onClick={handleLogout} style={{marginLeft:8}}>Logout</button>
            </div>
          </div>
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}
