import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { isAuthenticated, decodeToken, logout } from '../utils/auth';

export default function User(){
  const [claims, setClaims] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    setClaims(decodeToken());
  }, []);

  function handleLogout(){
    logout();
    navigate('/');
  }

  if (!isAuthenticated()){
    return (
      <div className="card auth">
        <h2>Not signed in</h2>
        <p>You need to <Link to="/">login</Link> to view your profile.</p>
      </div>
    );
  }

  return (
    <div className="card auth">
      <h2>Your Profile</h2>
      {claims ? (
        <div style={{textAlign:'left'}}>
          <div><strong>Email:</strong> {claims.sub || claims.identity || claims.email}</div>
          {claims.exp && <div><strong>Token expires:</strong> {new Date(claims.exp * 1000).toLocaleString()}</div>}
          <div style={{marginTop:12}}>
            <button className="btn primary" onClick={() => navigate('/booking')}>My Bookings</button>
            <button className="btn ghost" onClick={handleLogout} style={{marginLeft:8}}>Logout</button>
          </div>
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}
